from typing import List
class Solution:
    def rangeSum(self, nums: List[int], n: int, left: int, right: int) -> int:
        _sum = [i for i in nums]
        for i in range(2, len(nums)+1):
            temp = 0
            for j in range(i):
                temp += nums[j]
            _sum.append(temp)

            for j in range(i, len(nums)):
                temp -= nums[j-i]
                temp += nums[j]
                _sum.append(temp)

        _sum.sort()
        
        return sum(_sum[left-1:right]) % (10 ** 9 + 7)

class Solution:
    def rangeSum(self, nums: List[int], n: int, left: int, right: int) -> int:
        _sum = []
        for i in range(n):
            temp = 0
            for j in range(i, n):
                temp += nums[j]
                _sum.append(temp)

        _sum.sort()
        
        return sum(_sum[left-1:right]) % (10 ** 9 + 7)
    
